one.data <- data.frame(revigo.data)
names(one.data) <- revigo.names

one.data <- one.data [(one.data$plot_X != "null" & one.data$plot_Y != "null"), ]
one.data$plot_X <- as.numeric( as.character(one.data$plot_X) )
one.data$plot_Y <- as.numeric( as.character(one.data$plot_Y) )
one.data$log_size <- as.numeric( as.character(one.data$log_size) )
one.data$value <- as.numeric( as.character(one.data$value) )
one.data$frequency <- as.numeric( as.character(one.data$frequency) )
one.data$uniqueness <- as.numeric( as.character(one.data$uniqueness) )
one.data$dispensability <- as.numeric( as.character(one.data$dispensability) )
#head(one.data);

terms <- read.csv("tables/Supp_table_3.csv") %>% 
  filter(sample == "Devel") %>% 
  filter(P.DE < 0.05)

term_color <- terms[, c("ID", "module")] %>% 
  mutate(module = str_remove(module, "^\\d+_")) %>% 
  rename("term_ID" = "ID")

module_order <- c("magenta","pink","lightyellow","red","black","green","greenyellow",
                  "grey60","tan","yellow","salmon","darkgreen","royalblue","blue",
                  "lightcyan","purple","midnightblue","cyan","brown","turquoise","lightgreen","darkred")

one.data <- left_join(one.data, term_color, by = "term_ID") %>% 
  dplyr::arrange(dispensability) %>% 
  mutate(module = factor(module, levels = module_order))

ex <- one.data [ one.data$dispensability < 0.15, ];
one.x_range = max(one.data$plot_X) - min(one.data$plot_X);
one.y_range = max(one.data$plot_Y) - min(one.data$plot_Y);

p1 <- ggplot(data = one.data,
             aes(x = plot_X,
                 y = plot_Y,
                 color = module,
                 size = -value,
                 label = description)) +
  geom_point(pch = 21) +
  scale_color_manual(values = module_order) +
  scale_size( range=c(2, 16)) +
  theme_bw() +
  labs (x = "semantic space x", y = "semantic space y") +
  theme(legend.key = element_blank()) + 
  xlim(min(one.data$plot_X)-one.x_range/200,max(one.data$plot_X)+one.x_range/200) +
  ylim(min(one.data$plot_Y)-one.y_range/200,max(one.data$plot_Y)+one.y_range/200);

plotly::ggplotly(p1)


pdf("figures/Supp_Fig_2_revigo_semantic_space_galgal.pdf", paper = "a4", height = 8, width = 8)
p1 + geom_point() +
  theme(legend.position = "none")
dev.off()

# Facet by module

facet <- ggplot(data = one.data[,c(2,4,5,7)], 
                aes(x = plot_X,
                    y = plot_Y,
                    size = -value,
                    label = description)) +
  geom_point(color= "grey90") +
  geom_point(data = one.data,
             aes(x = plot_X,
                 y = plot_Y,
                 size = -value,
                 fill = module),
             pch = 21,
             color = "black") +
  scale_fill_manual(values = module_order) +
  scale_size( range=c(0.5, 8)) +
  theme_classic() +
  labs (x = "semantic space x", y = "semantic space y") +
  theme(legend.key = element_blank()) + 
  xlim(min(one.data$plot_X)-one.x_range/200,max(one.data$plot_X)+one.x_range/200) +
  ylim(min(one.data$plot_Y)-one.y_range/200,max(one.data$plot_Y)+one.y_range/200) + 
  facet_wrap(vars(module), nrow = 4, ncol = 6) + 
  theme(legend.position = "none")

pdf("figures/Fig_2_revigo_semantic_space_galgal_facet.pdf", height = 8, width = 10)
facet
dev.off()